mapper.select:
select
id, name, imag, price, desc, sales, typeId, disc, shopId
from dishes


        and id = #{id}


        and name = #{name}


        and imag = #{imag}


        and price = #{price}


        and desc = #{desc}


        and sales = #{sales}


        and typeId = #{typeid}


        and disc = #{disc}


        and shopId = #{shopid}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from dishes


        and id = #{id}


        and name = #{name}


        and imag = #{imag}


        and price = #{price}


        and desc = #{desc}


        and sales = #{sales}


        and typeId = #{typeid}


        and disc = #{disc}


        and shopId = #{shopid}
