mapper.select:
select
id, addressId, address, disc, time, score, type
from order


        and id = #{id}


        and addressId = #{addressid}


        and address = #{address}


        and disc = #{disc}


        and time = #{time}


        and score = #{score}


        and type = #{type}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from order


        and id = #{id}


        and addressId = #{addressid}


        and address = #{address}


        and disc = #{disc}


        and time = #{time}


        and score = #{score}


        and type = #{type}
